import Link from "next/link";
import { userActions } from "../actions";
import { connect } from "react-redux";
import { useForm } from "react-hook-form";
import { pattern, errorMessage } from '../constants';
import { closeModal } from "../utils/functions";

const UpdateUserModal = ({children, pageProps, updateUserError, updateUserSuccess, updateUser, selectedUser}) => {
    const {register, handleSubmit,reset, errors} = useForm();
    const onSubmit = formData => {
        updateUser(formData);

        // reset form
        if (!updateUserError){
            reset()
            closeModal("updateUserModal")

        }


    }
    return <div id="updateUserModal" className="modal">

        <div className="modal-content">
            <span className="close" data-type="hide-modal" data-target="updateUserModal">&times;</span>
            <h3>Update User</h3>
            <hr className="divider"/>

            { updateUserError && updateUserError.length > 0 && <div className="panel code error-msg">
                  <ul>
                    <li> {updateUserError}</li>    
                  </ul>

                </div>

            }
            { Object.keys(errors).length != 0 && 
                <div className="panel code error-msg">
                  <ul>

                    {errors.firstName && <li> {errors.firstName.message} </li> }
                    {errors.lastName && <li> {errors.lastName.message} </li> }

                    {errors.email && <li> {errors.email.message}</li> }
                    {errors.phoneNumber && <li> {errors.phoneNumber.message} </li> }

                  </ul>

                </div>
            }

            { selectedUser &&
            <form className="pure-form pure-form-aligned" onSubmit={handleSubmit(onSubmit)}>
              <fieldset>
                  <div className="pure-control-group">
                      <label htmlFor="Firstname">Firstname</label>
                      <input name="firstName" type="text" id="Firstname" placeholder="FirstName" defaultValue={selectedUser.firstName} ref={
                          register({
                              required:"FirstName is required"
                          })
                      } />
                  </div>
                  <div className="pure-control-group">
                      <label htmlFor="Lastname">Lastname</label>
                      <input name="lastName" type="text" id="Lastname" placeholder="LastName" value={selectedUser.lastName} ref={
                          register({
                              required:"LastName is required"
                          })
                      } />
                  </div>

                  <div className="pure-control-group">
                      <label htmlFor="Email">Email Address</label>
                      <input type="email" name="email" id="Email" placeholder="Email Address" value={selectedUser.email} ref={register({required:"Email is required", 
                            pattern:{
                            value:pattern.EMAIL,
                            message:errorMessage.INVALID_EMAIL
                            }
                        })} />
                  </div>

                  <div className="pure-control-group">
                      <label htmlFor="Phone">Phone Number</label>
                      <input type="tel" name="phoneNumber" pattern="^(\+1[0-9]{3}|[0-9]{3})[0-9]{3}[0-9]{4}$" id="Phone" placeholder="Phone number" value={selectedUser.phoneNumber} ref={register({required:"Phone Number is required", 
                            pattern:{
                            value:pattern.PHONE,
                            message:errorMessage.INVALID_PHONE

                            }
                        })} />
                  </div>

                  <div className="pure-controls">
                      <button type="submit" className="pure-button button-secondary">Submit</button>
                  </div>
                </fieldset>
              </form>
             }

        </div>
    </div>
}


const mapStateToProps = state => ({
    updateUserError: state.users.updateUserError,
    updateUserSuccess: state.users.updateUserSuccess,
    selectedUser: state.users.selectedUser
  });

const mapDispatchToProps = {
    updateUser: userActions.updateUser
};

export default connect(mapStateToProps, mapDispatchToProps)(UpdateUserModal)
