import Head from 'next/head'
import styles from '../styles/Home.module.css'
import LoginLayout from '../components/layouts/loginLayout'
import Link from 'next/link'
import { useForm } from "react-hook-form";
import { connect } from 'react-redux';

const LoginPage = ({loginError, pageProps}) => {

  const {register, handleSubmit, errors} = useForm();
  const onSubmit = formData => {

    dispatch(userActions.login(this.state.username, this.state.password));

  }

  const emailPattern = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
  const passwordPattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*])(?=.{8,})/

  return (

    <>
      <Head>
        <title>Admin | Login</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

          <div  className="content code">
            <p className={styles.description}>Administrative login</p>
            { loginError && <div className="panel code error-msg">
                  <ul>
                    <li> Invalid Login</li>    
                  </ul>

                </div>

            }
            { Object.keys(errors).length != 0 && 
                <div className="panel code error-msg">
                  <ul>
                    {errors.email && <li> Invalid Email Address</li> }
                    {errors.password && <li> Password must be minimum of 8 characters, with Uppercase, Lowercase and Symbol. </li> }

                  </ul>

                </div>
            }

            <form className="pure-form pure-form-aligned" onSubmit={handleSubmit(onSubmit)}>
            <fieldset>


                <div className={`pure-control-group ${errors.email && 'error-input'}`} >
                    <label htmlFor="email">Email Address</label>
                    <input 
                    type="email" ref={register({required:true, pattern:emailPattern})} 
                    name="email" id="email" placeholder="Email Address" />
                    {/*<span className="pure-form-message-inline">This is a required field.</span>*/}
                </div>

                <div className={`pure-control-group ${errors.password && 'error-input'}`}>
                    <label htmlFor="password">Password</label>
                    <input className={` ${errors.password && 'error'}`} 
                    type="password" ref={register({required:true, pattern:passwordPattern})} 
                    name="password" id="password" placeholder="Password" />
                </div>

                <div className="pure-controls">

                    <button type="submit" className="pure-button button-secondary">Submit</button>

                </div>
            </fieldset>
          </form>
          </div>





    </>
  )
}

LoginPage.Layout = LoginLayout

const mapStateToProps = state => ({
  loginError: !state.loggedIn && state.error
});


export default connect(mapStateToProps)(LoginPage)
