import glob from "glob"
import path from "path"

import "../styles/pure/pure-min.css"
import "../styles/pure/grids-responsive-min.css"
import "../styles/pure/main-grid.css"
import "../styles/pure/main.css"

import "../styles/globals.css"



import AdminLayout from "../components/layouts/adminLayout";
import { Provider } from "react-redux"
import { PersistGate } from "redux-persist/integration/react"
import withReduxStore from "../lib/with-redux-store";
import App, {Container} from 'next/app'
import { userConstants, routeConstants } from '../constants';
import { userActions } from "../actions"
import fetch from "isomorphic-unfetch";
import { route } from "next/dist/next-server/server/router"
import { getPageList } from "../utils/helpers"
function MyApp({ Component, pageProps, reduxStore, persistor }) {
  const Layout = Component.Layout || AdminLayout;


  return (
    <>
    <Provider store={reduxStore}>
      <PersistGate loading={null} persistor={persistor}>
        <Layout {...pageProps}>
          <Component {...pageProps} />
        </Layout>
      </PersistGate>
    </Provider>

      <script type="text/javascript" src="/js/menu.js" />
      <script type="text/javascript" src="/js/ui.js" />
      <script type="text/javascript" src="/js/grid.js" />
    </>
  )
}

MyApp.getInitialProps = async (appContext) => {

    let pathname = appContext.router.pathname

    const pagesDir = path.resolve(`${process.cwd()}/src/pages`)
    let pages = glob.sync(`${pagesDir}/**/*.js`)
    pages = pages.map(p => p.replace(pagesDir, '').replace('.js',''))
    pages = pages.filter(p => (p !== '/_app' && p !== '/index'))


    const currentUser = true
    const response = await fetch(routeConstants.REFRESH_TOKEN,{
      method:"POST",
      credentials:'include'
    },[])
    const result = await response.json()
    const accessToken = result.data?.accessToken;




    appContext.ctx.reduxStore.dispatch(userActions.defaultUser("babatunde", "obabs78@gmail.com"))
    const appProps = await App.getInitialProps(appContext);

    appContext.ctx.pages = pages
    console.log(`current pathname is ${pathname}`)
    if(!pages.includes(pathname) || pathname === '/notfound'){
      console.log('path doesnt exist')
      if (pathname !== '/notfound') {
        appContext.ctx.res.writeHead(302, { Location: "/404" });
        appContext.ctx.res.end();
      }
    }
    else if (accessToken && accessToken !== ""){
      appContext.ctx.reduxStore.dispatch(userActions.setAccessToken(accessToken))

      // check that we are in SSR mode (NOT static and NOT client-side)
      if (typeof window === "undefined" && appContext.ctx.res.writeHead) {


        // if path is / or login and we already have accessToken then change to /home
        if (pathname === '/login' || pathname === '/' || pathname === '/index') {
          pathname = '/home'  
        }

        //route to the path
        appContext.ctx.res.writeHead(302, { Location: pathname });
        appContext.ctx.res.end();

      }
    }else { 
      // no accessToken, route to login, if path is not already on /login 

      if (typeof window === "undefined" && appContext.ctx.res.writeHead) {
        if (pathname !== '/login') {
            appContext.ctx.res.writeHead(302, { Location: "/login" });
            appContext.ctx.res.end();
        }

      }
    }

    return { ...appProps};
}

export default withReduxStore(MyApp)
