import Head from 'next/head'
import Link from 'next/link'
import { connect } from 'react-redux';
import styles from '../styles/Home.module.css'
import { userActions, authActions } from "../actions";
import NewUserModal from '../components/newUserModal';
import { useEffect, useState } from 'react';

function AdminPage({admin, loading, error,allUsers, filteredUsers, getAllUsers, selectUser, selectedUser}) {

  useEffect(() => {
      getAllUsers()

  },[])

  const [searchFormData, setSearchFormData] = useState(Object.freeze({
    query:""
  }))


  //let [filteredUsers, setFilteredUsers] = useState(allUsers)

  // let [filteredUsers, setFilteredUsers] = useState(() => {
  //   if (allUsers && allUsers.length > 0){
  //       const filtered = allUsers.sort((a,b)=> new Date(b.createdAt) - new Date(a.createdAt))
  //       return filtered
  //     } else {
  //       return []
  //     }

  // })

  const onSearchTextChange = (e) => {
    setSearchFormData({
      ...searchFormData,
      [e.target.name]:e.target.value
    })

    if (e.target.value == "" || e.target.value == null){

    }
  }


  const clicked = (e) => {
    selectUser(e.target.dataset.uid)
  }

  const searchUser = (e) => {
    e.preventDefault()
    const filtered = allUsers.filter(user => {
      if (user.email.includes("babs"))
          return user
      })
    //setFilteredUsers(filtered)

    return false
  }

  const trClicked = (id) => {
    selectUser(id)
  }
  return (
    <>
      <Head>
        <title>Welcome | Admin</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <div className="pure-g">
        <div className="pure-u-3-5 padded">
            <div className="container bordered" style={{minHeight:400}}>
              <div className="">
              <div className="pure-menu pure-menu-horizontal">
            <ul className="pure-menu-list">
                {
                  /*
                      <li className="pure-menu-item">

                        <span href="" className="pure-menu-heading">Users</span>


                    </li>
                  */
                }

                <li className="pure-menu-item  pure-menu-has-children pure-menu-allow-hover">
                    <a href="#" id="menuLink1" className="pure-menu-link">USERS</a>
                    <ul className="pure-menu-children">
                        <li className="pure-menu-item">
                            <a href="#" id="myBtn" data-type="show-modal" data-target="myModal" className="pure-menu-link">Add New</a>
                        </li>
                        <li className="pure-menu-item">
                            <Link href="/changePassword">
                            <a href="#" className="pure-menu-link">Change Password</a>
                            </Link>
                        </li>
                        <li className="pure-menu-item">
                            <a href="#"  className="pure-menu-link">Logout</a>
                        </li>
                    </ul>
                </li>
                <li className="pure-menu-item right" style={{padding:"8px 8px 0px 0px"}} >
                  <form className="pure-form search-form" onSubmit={searchUser}>
                        <input type="text" name="search" placeholder="search user" className="pure-input" />
                      <button type="submit" className="pure-button">Search</button>
                  </form>
                </li>


            </ul>
        </div>

              </div>
              <hr className="divider"/>
              <table className="pure-table pure-table-horizontal">
                  <thead>
                      <tr>
                          <th>Select</th>
                          <th>FirstName</th>
                          <th>LastName</th>
                          <th>Phone</th>
                          <th>Email</th>
                      </tr>
                  </thead>
                  <tbody>
                    {loading && 
                        <tr>
                          <td colSpan="5" className="center-content">...loading</td>
                        </tr>
                    }

                  {!loading && error &&
                        <tr>
                          <td colSpan="5" className="center-content">An Error occured!</td>
                        </tr>
                    }

                    {!loading && filteredUsers && filteredUsers.length == 0 &&
                        <tr>
                        <td colSpan="5" className="center-content">No user record</td>
                      </tr>
                    }

                    {filteredUsers && filteredUsers.length > 0 &&
                      filteredUsers.map((user, i) => (
                        <tr key={i} data-ui={user.id} onClick={() => trClicked(user.id)}>
                          <td>
                            <label htmlFor="checkbox-radio-option-three" className="pure-radio">
                            <input data-uid={user.id} onClick={clicked} onChange={()=>false} checked={selectedUser && user.id === selectedUser.id || ""}type="radio" id="checkbox-radio-option-three" name="optionsRadios" value="option2" /></label>
                          </td>
                          <td>{user.firstName}</td>
                          <td>{user.lastName}</td>
                          <td>{user.phoneNumber}</td>
                          <td>{user.email}</td>
                        </tr>
                      ))
                    }


                  </tbody>
              </table>
            </div>

        </div>
        <div className="pure-u-2-5 padded">
        {selectedUser && 
          <div className="container bordered">
            <p>FirstName: {selectedUser.firstName}</p>
            <p>LastName: {selectedUser.lastName}</p>
            <p>Email: {selectedUser.email}</p>
            <p>Phone: {selectedUser.phoneNumber}</p>
          </div>
        }

        {!selectedUser && 
          <div className="container bordered center-content">
              <span>No User Selected!</span>
          </div>
        }

        </div>
      </div>
      <NewUserModal/>



    </>
  )
}


const mapStateToProps = state => {
  return {
    admin: state.authentication.user,
    allUsers: state.users.allUsers,
    loading: state.users.loading,
    error: state.users.error,
    selectedUser:state.users.selectedUser
  }

};

const mapDispatchToProps = {
  login: authActions.login,
  logout: authActions.logout,
  getAllUsers: userActions.getAllUsers,
  selectUser: userActions.selectUser,
  filter

};

export default connect(mapStateToProps, mapDispatchToProps)(AdminPage)
