import { userConstants } from '../constants';

import UserService from '../services/UserService';

export const userActions = {
    addUser,
    getAllUsers,
    selectUser,
    searchUser,
    delete: _delete
};

const userService = new UserService()


function addUser(data) {
    return (dispatch, getState) => {
        const {token} = getState().authentication;
        //dispatch(request(data));

        userService.addUser(data, token)
            .then(
                result => { 
                    //console.log(JSON.stringify(result))
                    dispatch(success(result.data.user));

                },
                error => {
                    dispatch(failure(error.toString()));
                }
            );
    };

    function request(user) { return { type: userConstants.REGISTER_REQUEST, user } }
    function success(user) { return { type: userConstants.REGISTER_SUCCESS, user } }
    function failure(error) { return { type: userConstants.REGISTER_FAILURE, error } }
}

function getAllUsers() {
    return (dispatch, getState) => {
        dispatch(request());
        const token = getState().authentication.token
        userService.getAllUser(token)
            .then(

                result => dispatch(success(result.data.users)),
                error => dispatch(failure(error.toString()))
            );
    };

    function request() { return { type: userConstants.GETALL_REQUEST } }
    function success(users) { 
        return { type: userConstants.GETALL_SUCCESS, users } }
    function failure(error) { return { type: userConstants.GETALL_FAILURE, error } }
}

function selectUser(userId) {
    return (dispatch) => {
        dispatch({ type: userConstants.SELECT_USER, userId });

    };

}

function reset(userId) {
    return (dispatch) => {
        dispatch({ type: userConstants.SELECT_USER, userId });

    };

}

function searchUser(query) {
    return (dispatch, getState) => {
        const all = getState().users.allUsers

        if (query && query != ""){
            const q = query.split(" ")
            const filtered = all.filter(user => {
                if (q[0] && user.email.includes(q[0])){
                    return user
                }

                if (q[1] && user.email.includes(q[1])){
                    return user
                }

                if (q[0] && user.firstName.includes(q[0])){
                    return user
                }

                if (q[1] && user.firstName.includes(q[1])){
                    return user
                }

                if (q[0] && user.lastName.includes(q[0])){
                    return user
                }

                if (q[1] && user.lastName.includes(q[1])){
                    return user
                }

            })

            dispatch({ type: userConstants.FILTERED_USER, users: filtered })

        } else {

            dispatch({ type: userConstants.FILTERED_USER, user: all })
        }

    };

}

// prefixed function name with underscore because delete is a reserved word in javascript
function _delete(id) {
    return dispatch => {
        dispatch(request(id));

        userService.delete(id)
            .then(
                user => dispatch(success(id)),
                error => dispatch(failure(id, error.toString()))
            );
    };

    function request(id) { return { type: userConstants.DELETE_REQUEST, id } }
    function success(id) { return { type: userConstants.DELETE_SUCCESS, id } }
    function failure(id, error) { return { type: userConstants.DELETE_FAILURE, id, error } }
}
