import Link from "next/link";
import { userActions } from "../actions";
import { connect } from "react-redux";
import { useForm } from "react-hook-form";
import { pattern, errorMessage } from '../constants';

const NewUserModal = ({children, pageProps, logout}) => {
    const {register, handleSubmit,reset, errors} = useForm();
    const onSubmit = formData => {
        login(formData.email, formData.password);

    }
    return <div id="myModal" className="modal">

        <div className="modal-content">
            <span className="close" data-type="hide-modal" data-target="myModal">&times;</span>
            <h3>New User</h3>
            <hr className="divider"/>

            <form className="pure-form pure-form-aligned">
              <fieldset>
                  <div className="pure-control-group">
                      <label htmlFor="Firstname">Firstname</label>
                      <input name="firstName" type="text" id="Firstname" placeholder="FirstName" />
                  </div>
                  <div className="pure-control-group">
                      <label htmlFor="Lastname">Lastname</label>
                      <input name="lastName" type="text" id="Lastname" placeholder="LastName" re />
                  </div>

                  <div className="pure-control-group">
                      <label htmlFor="Email">Email Address</label>
                      <input type="email" id="Email" placeholder="Email Address" ref={register({required:"Email is required", 
                            pattern:{
                            value:pattern.EMAIL,
                            message:errorMessage.INVALID_EMAIL

                            }
                        })} />
                  </div>

                  <div className="pure-control-group">
                      <label htmlFor="Phone">Phone Number</label>
                      <input type="tel" name="phoneNumber" pattern="^(\+1[0-9]{3}|[0-9]{3})[0-9]{3}[0-9]{4}$" id="Phone" placeholder="Phone number" />
                  </div>

                  <div className="pure-controls">
                      <button type="submit" className="pure-button button-secondary">Submit</button>
                  </div>
                </fieldset>
              </form>


        </div>
    </div>
}



const mapDispatchToProps = {
    logout: userActions.logout
};

export default connect(null, mapDispatchToProps)(NewUserModal)
