import { pattern, errorMessage } from '../constants';
import Papa from "papaparse"

const UploadReportModal = ({children, pageProps,addUserError, addUser,selectedUser, lastAdded}) => {
    const {register, handleSubmit,reset, errors} = useForm();
    const parseCsv = (data) => {

        let parsedata = [];

        let newLinebrk = data.split("\n");

        for(let i = 0; i < newLinebrk.length; i++) {

            parsedata.push(newLinebrk[i].split(","))

        }

        const header = parsedata[0]
        const records = parsedata.slice(1, -2)
        console.log(records)

        return parsedata
    }

    const processData = (headers, body ) => {
        console.log(headers)
        console.log(body)
    }
    const onSubmit = formData => {
        const file = formData.selectedFile[0]

        let data = null;

        Papa.parse(file, {
            header:true,
            skipEmptyLines:true,

            complete: result => {
                if (result.errors.length != 0)
                    throw Error(JSON.stringify())
                processData(result.meta.fields, result.data)

            }
        })


        console.log(data)


        //addUser(formData);


        // if (!addUserError){
        //     reset()

        // }


    }
    return <div id="uploadReportModal" className="modal">

        <div className="modal-content">
            <span className="close" data-type="hide-modal" data-target="uploadReportModal">&times;</span>
            {selectedUser && <h3>Upload a report for <span className="blue">{selectedUser.firstName + " " + selectedUser.lastName}</span></h3> }
            <hr className="divider"/>
            {lastAdded && !addUserError && (Object.keys(errors).length == 0) && <div className="panel code">
            <h4 className="success"> LAST RECORD: {lastAdded.firstName + " " + lastAdded.lastName}</h4>
