                    <li> {error}</li>    
                  </ul>

                </div>

            }
            { Object.keys(errors).length != 0 && 
                <div className="panel code error-msg">
                  <ul>

                    {errors.firstName && <li> {errors.firstName.message} </li> }
                    {errors.lastName && <li> {errors.lastName.message} </li> }

                    {errors.email && <li> {errors.email.message}</li> }
                    {errors.phoneNumber && <li> {errors.phoneNumber.message} </li> }

                  </ul>

                </div>
            }

            <form className="pure-form pure-form-stacked" onSubmit={handleSubmit(onSubmit)}>
              <fieldset>
                  <div className="pure-control-group">
                      <label htmlFor="selectedFile">Select file</label>
                      <input name="selectedFile" required={true} type="file" id="selectedFile" placeholder="choose report file (csv)" ref={register} />
                  </div>
                  <br/>

                  <div className="pure-controls">
                      <button type="submit" className="pure-button button-secondary">Submit</button>
                  </div>
                </fieldset>
              </form>


        </div>
    </div>
}


const mapStateToProps = state => ({
    addUserError: state.users.addUserError,
    lastAdded: state.users.lastAdded,
    selectedUser: state.users.selectedUser
  });

const mapDispatchToProps = {
    addUser: userActions.addUser
};

export default connect(mapStateToProps, mapDispatchToProps)(UploadReportModal)
